#ifndef U3V_LSC_CCM_REG_H_
#define U3V_LSC_CCM_REG_H_

// CCM lattice IP core register set
// We are left shifting the offset by 2 (multiply by 4), for word offset
#define CCM_CONTROL          (0x00)
#define CONFIGURATION_UPDATE (0x01 << 2)
#define TRANSLATION_R        (0x02 << 2)
#define TRANSLATION_G        (0x03 << 2)
#define TRANSLATION_B        (0x04 << 2)
#define MRR                  (0x05 << 2)
#define MRG                  (0x06 << 2)
#define MRB                  (0x07 << 2)
#define MGR                  (0x08 << 2)
#define MGG                  (0x09 << 2)
#define MGB                  (0x0A << 2)
#define MBR                  (0x0B << 2)
#define MBG                  (0x0C << 2)
#define MBB                  (0x0D << 2)
#define LINE_QUERY_ADDR      (0x0E << 2)
#define PIXEL_QUERY_ADDR     (0x0F << 2)
#define PIXEL_RED_ADDR       (0x10 << 2)
#define PIXEL_GREEN_ADDR     (0x11 << 2)
#define PIXEL_BLUE_ADDR      (0x12 << 2)

//IMP Note: CCM UG has incorrect value for enable/disable.
#define CCM_ENABLE           0x1
#define CCM_DISABLE          0x0

//For the nine matrix coefficients MRR to MBB, the user has to multiply the coefficient
//values by 32768 and use the integer part when configuring the CSR dynamically.
//For example, for an MRR=0.87, the user must give 28508 (0.87*32768=28508.16)
#define GET_CCM_COEFF_INT(n) (n*32768)/100  //Value of n must be [-99 to +99]

//Value of Following nine matrix coefficients MRR to MBB must be in range of [-99 to +99]
#define MRR_VALUE            80
#define MRG_VALUE            0
#define MRB_VALUE            -10
#define MGR_VALUE            0
#define MGG_VALUE            60
#define MGB_VALUE            0
#define MBR_VALUE            0
#define MBG_VALUE            0
#define MBB_VALUE            99


#define TRANSLATION_R_VALUE  0
#define TRANSLATION_G_VALUE  0
#define TRANSLATION_B_VALUE  0

uint8_t lsc_ccm_init(uint32_t);

#endif /* U3V_LSC_CCM_REG_H_ */
/** @} */