static unsigned char mode_table_common[] = {
  /* software reset */
  SHORT_TO_CHAR(REG_MODE_SEL), 	0x00,		//0x01, 0x00, 0x00,
  SHORT_TO_CHAR(REG_SW_RESET), 	0x01,		//0x01, 0x03, 0x01,
  SHORT_TO_CHAR(REG_MODE_SEL), 	0x00,		//0x01, 0x00, 0x00,

  SHORT_TO_CHAR(0x6621), 	   	0x01,		//0x66, 0x21, 0x01,
  SHORT_TO_CHAR(0x6623),	   	0x01,		//0x66, 0x23, 0x01,

  /*Access Code to registers over 0x3000 */
  SHORT_TO_CHAR(0x30EB),	   	0x05,		//0x30, 0xEB, 0x05,
  SHORT_TO_CHAR(0x30EB), 	   	0x0C,		//0x30, 0xEB, 0x0C,
  SHORT_TO_CHAR(0x300A),	 	0xFF,		//0x30, 0x0A, 0xFF,
  SHORT_TO_CHAR(0x300B),	 	0xFF,		//0x30, 0x0B, 0xFF,
  SHORT_TO_CHAR(0x30EB),	 	0x05,		//0x30, 0xEB, 0x05,
  SHORT_TO_CHAR(0x30EB),	 	0x09,		//0x30, 0xEB, 0x09,

  /* number of csi lanes = 2 (1 + 1) */
  SHORT_TO_CHAR(REG_CSI_LANE),	0x01,		//0x01, 0x14, 0x01,

  /* dphy control: Auto */
  SHORT_TO_CHAR(REG_DPHY_CTRL),	0x00,		//0x01, 0x28, 0x00,

  /* external clock frequency = 24MHz */
  SHORT_TO_CHAR(REG_EXCK_FREQ_MSB),	0x18,		//0x01, 0x2A, 0x18, // 24 MHz
  SHORT_TO_CHAR(REG_EXCK_FREQ_LSB), 0x00,		//0x01, 0x2B, 0x00, // 000 KHz

  //Clock Settings

  /* CSI data format */
  SHORT_TO_CHAR(REG_CSI_FORMAT_C),	0x0A,		//0x01, 0x8C, 0x0A,
  SHORT_TO_CHAR(REG_CSI_FORMAT_D),	0x0A,		//0x01, 0x8D, 0x0A,

  // PIXEL CLOCK USED TO DRIVE SENSOR)
  // VTPXCK_DIV: 4: 1/4, 5: 1/5, 8: 1/8, 10: 1/10
  SHORT_TO_CHAR(REG_VTPXCK_DIV),	0x05,		//0x03, 0x01, 0x05,
  // VTSYS_DIV = 1: Divide by 1
  SHORT_TO_CHAR(REG_VTSYCK_DIV), 	0x01,		//0x03, 0x03, 0x01,

  // PREPLLCK_VT_DIV: 1: 1/1, 2: 1/2, 3: 1/3
  SHORT_TO_CHAR(REG_PREPLLCK_VT_DIV), 0x03,		//0x03, 0x04, 0x03, //MUST BE 3 FOR 24MHz

  // PREPLLCK_OP_DIV: 1: 1/1, 2: 1/2, 3: 1/3
  SHORT_TO_CHAR(REG_PREPLLCK_OP_DIV), 0x03,		//0x03, 0x05, 0x03, //MUST BE 3 FOR 24MHz

  // PLL_VT_MPY = 57 (Multiplier)
  // PLL1
  SHORT_TO_CHAR(REG_PLL_VT_MPY_MSB), 0x00,		//0x03, 0x06, 0x00,
  SHORT_TO_CHAR(REG_PLL_VT_MPY_LSB), 0x14,		//0x03, 0x07, 0x14,

  // OPPXCK_DIV (pixel bit depth): 8:1/8 10:1/10
  SHORT_TO_CHAR(REG_OPPXCK_DIV), 0x0A,		//0x03, 0x09, 0x0A,

  // OPSYSCK_DIV (divide by two for double data rate): 1:1/2
  SHORT_TO_CHAR(REG_OPSYCK_DIV), 0x01,		//0x03, 0x0B, 0x01,

  // PLL_OP_MPY = 90 (Multiplier)
  // PLL2
  SHORT_TO_CHAR(REG_PLL_OP_MPY_MSB), 0x00,		//0x03, 0x0C, 0x00,
  SHORT_TO_CHAR(REG_PLL_OP_MPY_LSB), 0x28,		//0x03, 0x0D, 0x28,


  //Sensor Pixel Clock: EXCLK_FREQ / PREPLLLCK_VT_DIV * PLL_VT_MPY / VTPXCK_DIV: 24MHz / 3 * 57 / 5 = 91 MHz

  // CIS tunning
  SHORT_TO_CHAR(0x455E),		0x00,		//0x45, 0x5E, 0x00,
  SHORT_TO_CHAR(0x471E),		0x4B,		//0x47, 0x1E, 0x4B,
  SHORT_TO_CHAR(0x4767),		0x0F,		//0x47, 0x67, 0x0F,
  SHORT_TO_CHAR(0x4750),		0x14,		//0x47, 0x50, 0x14,
  SHORT_TO_CHAR(0x4540),		0x00,		//0x45, 0x40, 0x00,
  SHORT_TO_CHAR(0x47B4),		0x14,		//0x47, 0xB4, 0x14,
  SHORT_TO_CHAR(0x4713),		0x30,		//0x47, 0x13, 0x30,
  SHORT_TO_CHAR(0x478B),		0x10,		//0x47, 0x8B, 0x10,
  SHORT_TO_CHAR(0x478F),		0x10,		//0x47, 0x8F, 0x10,
  SHORT_TO_CHAR(0x4793),		0x10,		//0x47, 0x93, 0x10,
  SHORT_TO_CHAR(0x4797),		0x0E,		//0x47, 0x97, 0x0E,
  SHORT_TO_CHAR(0x479B), 		0x0E,		//0x47, 0x9B, 0x0E

};

